    private void boton_mostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_boton_mostrarActionPerformed
        // TODO add your handling code here:
        String nombre = campo_nombre.getText();
        String apellido = campo_apellido.getText();
        String edad_texto = campo_edad.getText();
        int edad = 0;
        try {
            edad = Integer.parseInt(edad_texto);
            vendedor vendedor1 = new vendedor(nombre, apellido, edad);
            vendedor1.verificarEdad(edad);
            label_nombre.setText("Nombre: " + vendedor1.nombre);
            label_apellido.setText("Apellido: " + vendedor1.apellido);
            label_edad.setText("Edad: " + vendedor1.edad);
            label_mensaje.setText("Datos mostrados correctamente.");
        } catch (NumberFormatException e) {
            label_mensaje.setText("Error: La edad debe ser un n√∫mero entero.");
        } catch (IllegalArgumentException e) {
            label_mensaje.setText("Error: " + e.getMessage());
        }
               
        
    }//GEN-LAST:event_boton_mostrarActionPerformed

